    // 볼륨이 켜져 있을 때 표시될 이미지.
    public Sprite volumeSprite;
    // 음소거 상태일 때 표시될 이미지.
    public Sprite muteSprite;

    public bool tutorial;

    Coroutine fadeCoroutine;

    // 게임에 관한 것
    public int nowWorld = 0; // 월드
    public int code; // 음악 인덱스
    public bool gameStart; // 게임이 시작헀는지

