            case 601: SetBecauseChristmas(); break;
            case 602: SetAIWFCIU(); break;
            case 603: SetChristmas(); break;
            case 604: SetButterfly(); break;
        }
      
        fadeCoroutine = StartCoroutine(PlayMusicWithFadeOut());
    }

    public NoteData getNoteData(int idx)
    {
        return new NoteData(beat[idx], pos[idx], length[idx]);
    }

    // 음악 미리듣기 
    IEnumerator PlayMusicWithFadeOut()
    {
        Debug.Log("시작하빈다");
        float startVolume = audioSource.volume; // 시작 볼륨
        while (true)
        {
            audioSource.volume = 0;
            // 음악 재생
            audioSource.Play();

            // 페이드 인
            for (float t = 0; t < 3.0f; t += Time.deltaTime)
            {
                audioSource.volume = Mathf.Lerp(0, startVolume, t / 3.0f);
                yield return null;
            }


            // 10초 대기
            yield return new WaitForSeconds(10);

            // 페이드아웃 시작

            for (float t = 0; t < 3.0f; t += Time.deltaTime)
            {
                audioSource.volume = Mathf.Lerp(startVolume, 0, t / 3.0f);
                yield return null;
            }

            // 음악 정지 및 볼륨 초기화
            audioSource.Stop();

            // 필요한 경우 음악 다시 재생
            audioSource.Play();
        }
    }

